import 'package:hlshop/all_file/all_file.dart';part 'product_item_model.g.dart';@JsonSerializable()class ProductItemModel {  @JsonKey(name: 'itemid')  final String? itemId;  final String? name;  @JsonKey(name: 'modelid')  final String? modelId;  @JsonKey(name: 'model_name')  final String? modelName;  @JsonKey(name: 'model_image')  final String? modelImage;  @JsonKey(      fromJson: priceUnitFromAny, toJson: priceUnitToJson, name: 'model_price')  final PriceUnit? modelPrice;  const ProductItemModel({    this.itemId,    this.name,    this.modelId,    this.modelName,    this.modelImage,    this.modelPrice,  });  factory ProductItemModel.fromJson(Map<String, dynamic> json) =>      _$ProductItemModelFromJson(json);  Map<String, dynamic> toJson() => _$ProductItemModelToJson(this);}extension ProductItemModelConverter on ProductItemModel {  ProductItemEntity toEntity() {    return ProductItemEntity(      id: itemId,      name: name,      modelID: modelId,      modelName: modelName,      modelImage: ImageEntity(        src: modelImage,      ),      modelPrice: modelPrice,    );  }}