import 'package:json_annotation/json_annotation.dart';part 'product_item_model.g.dart';@JsonSerializable()class ProductItemModel {  @JsonKey(name: 'itemid')  final String? itemId;  final String? name;  @JsonKey(name: 'modelid')  final String? modelId;  @JsonKey(name: 'model_name')  final String? modelName;  const ProductItemModel({    this.itemId,    this.name,    this.modelId,    this.modelName,  });  factory ProductItemModel.fromJson(Map<String, dynamic> json) =>      _$ProductItemModelFromJson(json);  Map<String, dynamic> toJson() => _$ProductItemModelToJson(this);}