import 'package:hlshop/all_file/all_file.dart';@RoutePage()class AllProductsPage extends StatelessWidget {  const AllProductsPage({    super.key,    this.title,    this.productListType,    this.fetchListData,  });  final Object? title;  final ProductListType? productListType;  final Future<List<ProductEntity>> Function(int, int)? fetchListData;  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppAppBar(        title: title,        args: const AppBarArgs(          centerTitle: false,          actions: [            ShoppingCartBtn(),          ],        ),      ),      body: ProductGridVer(        fetchListData: (int offset, int limit) {          return fetchListData != null              ? fetchListData!(offset, limit)              : getIt<ProductRepo>().getProductList(                  offset: offset,                  limit: limit,                  type: productListType,                  showType: ProductListShowType.homePage,                );        },      ).pyDefault(),    );  }}