import 'package:flutter_rating_bar/flutter_rating_bar.dart';import 'package:hlshop/all_file/all_file.dart';class ProductRating extends StatelessWidget {  const ProductRating({    super.key,    required this.productRatingSummary,    required this.onPressed,  });  final ProductRatingSummary? productRatingSummary;  final void Function()? onPressed;  @override  Widget build(BuildContext context) {    return Column(      children: [        AppTile(          title: 'Đánh giá sản phẩm'.tr().text.titleMedium(context).bold.make(),          padding: Dimens.edge,          subtitle: Row(            children: [              RatingBar.builder(                initialRating: productRatingSummary?.average ?? 0.0,                itemSize: 20,                minRating: 1,                allowHalfRating: true,                ignoreGestures: true,                unratedColor: context.theme.hintColor.withOpacity(0.5),                itemBuilder: (context, _) => Icon(                  Icons.star,                  color: context.themeColor.yellow,                ),                onRatingUpdate: print,              ),              '${productRatingSummary?.average}/${productRatingSummary?.total}'                  .text                  .colorDark(context)                  .make(),            ].withDivider(Gaps.hGap8),          ).pt2(),          onPressed: onPressed,        ),      ],    );  }}