import 'package:hlshop/all_file/all_file.dart';import 'package:smooth_page_indicator/smooth_page_indicator.dart';class ProductPhotosBody extends StatefulWidget {  const ProductPhotosBody({    super.key,    required this.imgList,    required this.currentIndex,  });  final List<String> imgList;  final int currentIndex;  @override  State<ProductPhotosBody> createState() => _ProductPhotosBodyState();}class _ProductPhotosBodyState extends State<ProductPhotosBody> {  late final AppPhotoViewController<String> _controller;  @override  void initState() {    _controller = AppPhotoViewController<String>(      imgList: widget.imgList,      imageRatio: 1 / 1,      itemStringLink: (String item) {        return item;      },      itemStringPreview: (String item) {        return item;      },    );    WidgetsBinding.instance.addPostFrameCallback((_) {      _controller.controller.jumpToPage(widget.currentIndex);    });    super.initState();  }  @override  Widget build(BuildContext context) {    return Stack(      children: [        InteractiveViewer(          minScale: 0.05,          child: Align(            child: AppPhotoView<String>(              photoController: _controller,            ),          ),        ),        Align(          alignment: Alignment.bottomCenter,          child: SmoothPageIndicator(            controller: _controller.controller,            count: _controller.listCarousel.length,            onDotClicked: (index) {              _controller.controller.jumpToPage(index);            },            effect: WormEffect(              dotWidth: Dimens.ic_XS4,              dotHeight: Dimens.ic_XS4,              activeDotColor: context.themeColor.primary,              dotColor: context.themeColor.greyLight,            ),          ),        ).pb16(),      ],    );  }}