import 'package:hlshop/all_file/all_file.dart';@RoutePage()class ProductPhotosPage extends StatelessWidget {  const ProductPhotosPage({    super.key,    required this.imgList,    required this.currentIndex,  });  final List<String> imgList;  final int currentIndex;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: context.themeColor.greyDarkest,      appBar: AppAppBar(        args: AppBarArgs(          backgroundColor: context.themeColor.greyDarkest,          elevation: 0,        ),      ),      body: ProductPhotosBody(        imgList: imgList,        currentIndex: currentIndex,      ),    );  }}