import 'package:hlshop/all_file/all_file.dart';part 'rating_item_entity.freezed.dart';@freezedclass RatingItemEntity with _$RatingItemEntity {  const RatingItemEntity._();  const factory RatingItemEntity({    String? id,    String? itemId,    String? comment,    int? ratingStar,    String? createdTime,    int? editable,    String? editableDate,    String? userID,    String? authorUserName,    String? authorPortrait,    List<ProductItemEntity>? productItems,    DetailRating? detailRating,    List<String>? images,    RatingReplyEntity? ratingReply,  }) = _RatingItemEntity;  static const ratingKey = 'ratingKey';}enum RatingFilterType {  all(0),  comment(1),  image(2),  star(0);  dynamic titleWidget(BuildContext context) => switch (this) {        RatingFilterType.all => 'Tất cả',        RatingFilterType.comment => 'Đánh giá',        RatingFilterType.image => 'Hình ảnh',        RatingFilterType.star => Row(            mainAxisAlignment: MainAxisAlignment.center,            children: [              'Sao'.tr().text.bodyMedium(context).make(),              const Icon(                PhosphorIcons.star_fill,                color: Colors.orangeAccent,                size: 15,              ),              const Icon(                PhosphorIcons.caret_down,                color: Colors.grey,                size: 20,              ),            ].withDivider(Gaps.hGap4),          ),      };  int amount(RatingSummary? ratingSummary) => switch (this) {        RatingFilterType.all => ratingSummary?.total ?? 0,        RatingFilterType.comment => ratingSummary?.countWithContext ?? 0,        RatingFilterType.image => ratingSummary?.countWithImage ?? 0,        RatingFilterType.star => ratingSummary?.total ?? 0,      };  const RatingFilterType(this.value);  final int? value;}enum RatingStarEnum {  one(1),  two(2),  three(3),  four(4),  five(5);  const RatingStarEnum(this.value);  final int? value;}