import 'package:hlshop/all_file/all_file.dart';abstract class RatingRepo {  Future<RatingEntity?> getRatingByProduct({    required String productId,    int? type,    int? filter,    int? offset,    int? limit,  });  Future<List<RatingItemEntity>> getRatingByUser(    int? offset,    int? limit,  );  Future<List<RatingItemEntity>> getRatingByOrder({    required String orderId,    int? offset,    int? limit,  });  Future<ImageEntity> uploadImage({    required File file,  });  Future<bool> create({    required RatingOrderReqEntity ratingOrderReqEntity,  });  Future<bool> update({    required OrderItemEntity orderItemEntity,  });}