import 'package:hlshop/all_file/all_file.dart';class RatingBody extends StatelessWidget {  const RatingBody({    super.key,    required this.orderDataEntity,  });  final OrderDataEntity orderDataEntity;  @override  Widget build(BuildContext context) {    return AppScrollBody.withBottomSpacing(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          OrderCode(orderDataEntity: orderDataEntity).pxDefault(),          ProductRatingSection(orderDataEntity: orderDataEntity),          AppButton(            onPressed: () {              context.read<RatingCubit>().submit();            },            label: 'Gửi đánh giá'.tr(),          ).centered().pb16(),        ].withDivider(Gaps.vGap16),      ).pt16(),    );  }}