import 'package:hlshop/all_file/all_file.dart';class RatingBody extends StatelessWidget {  const RatingBody({    super.key,    required this.orderDataEntity,  });  final OrderDataEntity orderDataEntity;  @override  Widget build(BuildContext context) {    final cubit = context.read<RatingCubit>();    return AppScrollBody.withBottomSpacing(      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          OrderCode(orderDataEntity: orderDataEntity).pxDefault(),          ProductRatingSection(orderDataEntity: orderDataEntity),          ServiceRow(            title: 'Dịch vụ nhà cung cấp:'.tr(),            initialRating: cubit.state.providerServiceRating,            onRatingUpdate: cubit.updateProviderRating,          ),          ServiceRow(            title: 'Dịch vụ vận chuyển:'.tr(),            initialRating: cubit.state.deliveryServiceRating,            onRatingUpdate: cubit.updateDeliverRating,          ),          ServiceRow(            title: 'Người giao hàng:'.tr(),            initialRating: cubit.state.shipperServiceRating,            onRatingUpdate: cubit.updateShipperRating,          ),          AppButton(            onPressed: () {              context.read<RatingCubit>().submit();            },            label: 'Gửi đánh giá'.tr(),          ).pxDefault(),        ].withDivider(Gaps.vGap16),      ).pt16(),    );  }}class ServiceRow extends StatelessWidget {  const ServiceRow({    super.key,    required this.title,    required this.initialRating,    required this.onRatingUpdate,  });  final String title;  final double initialRating;  final void Function(double) onRatingUpdate;  @override  Widget build(BuildContext context) {    return Row(      children: [        title.text.bodyMedium(context).make().expand(),        AppRatingBar(          onRatingUpdate: onRatingUpdate,          initialRating: initialRating,          itemSize: Dimens.ic_XS,          itemPadding: const EdgeInsets.symmetric(horizontal: Dimens.gap_dp4),        ),      ],    ).pxDefault();  }}