import 'package:hlshop/all_file/all_file.dart';@RoutePage()class RatingPage extends StatelessWidget {  const RatingPage({    super.key,    required this.orderDataEntity,  });  final OrderDataEntity orderDataEntity;  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => RatingCubit(        orderDataEntity: orderDataEntity,        orderProductList: orderDataEntity.orderProductList ?? [],      ),      child: ApiItemConsumer<RatingCubit, RatingState>(        getStatus: (state) => state.status,        onDone: (state) {          DialogUtils.showSuccessDialog(            context: context,            content: 'Bạn đã đánh giá thành công!'.tr(),            autoDismissDelay: 2.seconds,            onAutoDismiss: () {              context.router.pop(true);            },          );        },        child: Scaffold(          appBar: AppAppBar(            title: 'Viết đánh giá'.tr(),          ),          body: RatingBody(            orderDataEntity: orderDataEntity,          ),        ),      ),    );  }}