import 'package:app_ui_kit/all_file/app_ui_kit_all_file.dart';import 'package:hlshop/all_file/all_file.dart';class StarPickupBody extends StatelessWidget {  const StarPickupBody({    super.key,    required this.count,  });  final List<int?> count;  @override  Widget build(BuildContext context) {    return Column(      mainAxisSize: MainAxisSize.min,      children: [        Column(          mainAxisSize: MainAxisSize.min,          children: RatingStarEnum.values.reversed              .mapAsList(                (item) => AppRadioReactive<RatingStarEnum>(                  formControlName: 'ratingStarKey',                  value: item,                  labelWidget: Row(                    children: [                      ...List.generate(                        item.value ?? 0,                            (index) => const Icon(                          PhosphorIcons.star_fill,                          size: 15,                          color: Colors.orangeAccent,                        ),                      ),                     Gaps.hGap8,                     '${count[item.index]}'.tr().text.make(),                    ],                  ),                ),              )              .withDivider(const AppDivider.thin()),        ),        Gaps.vGap8,        Row(          children: [            AppButton(              style: AppButtonTheme.secondary(context),              child: 'Bỏ lọc'.tr().text.make(),              onPressed: () {                context.router.pop(0);              },            ).expand(),            Gaps.hGap8,            AppButton(              style: AppButtonTheme.primary(context),              isSubmitButton: true,              onPressed: () {                final formControl = context.getControl<RatingStarEnum>(                  'ratingStarKey',                );                context.router.pop(formControl?.value);              },              child: 'Đồng ý'.tr().text.make(),            ).expand(),          ],        )      ],    ).pDefault();  }}