import 'package:flutter_rating_bar/flutter_rating_bar.dart';import 'package:hlshop/all_file/all_file.dart';class AppRatingBar extends StatelessWidget {  const AppRatingBar({    super.key,    required this.onRatingUpdate,    this.allowHalfRating = false,    this.iconColor,    this.initialRating = 0.0,    this.itemPadding,    this.itemSize,    this.ignoreGestures = false,  });  final bool allowHalfRating;  final bool ignoreGestures;  final Color? iconColor;  final double initialRating;  final void Function(double) onRatingUpdate;  final EdgeInsetsGeometry? itemPadding;  final double? itemSize;  @override  Widget build(BuildContext context) {    return RatingBar.builder(      wrapAlignment: WrapAlignment.spaceAround,      initialRating: initialRating,      ignoreGestures: ignoreGestures,      itemSize: itemSize ?? Dimens.minRowHeight_XS,      minRating: 1,      itemPadding:      itemPadding ?? const EdgeInsets.symmetric(horizontal: Dimens.pad_XS2),      allowHalfRating: allowHalfRating,      unratedColor: context.themeColor.greyLighter,      itemBuilder: (context, _) => Icon(        Icons.star,        color: iconColor ?? context.themeColor.yellow,      ),      onRatingUpdate: onRatingUpdate,    );  }}