import 'package:hlshop/all_file/all_file.dart';class DisplayImagesAttachment extends StatelessWidget {  const DisplayImagesAttachment({    super.key,    this.showImageAttachBehind = false,    this.isCanWork = true,    this.isEnoughMedias = false,    required this.uploadImage,    required this.deleteImage,    required this.medias,    this.maxImage = 5,  });  final bool showImageAttachBehind;  final bool isCanWork;  final bool isEnoughMedias;  final int maxImage;  final void Function()? uploadImage;  final void Function(String? id) deleteImage;  final List<ImageEntity> medias;  @override  Widget build(BuildContext context) {    return LayoutBuilder(      builder: (BuildContext context, BoxConstraints constraints) {        final width = constraints.maxWidth * 0.23;        return Wrap(          crossAxisAlignment: WrapCrossAlignment.center,          spacing: Dimens.gap_dp8,          runSpacing: Dimens.gap_dp8,          children: [            if (medias.isEmpty && isCanWork)              ImagesAttachment(                amountMedia: medias.length,                maxMedia: maxImage,                uploadImage: uploadImage,                width: width,              ),            if (medias.isNotEmpty) ...[              ...medias.map(                (media) => Stack(                  alignment: Alignment.topRight,                  children: [                    SizedBox(                      height: 80,                      width: width,                      child: AppImg(                        media.src,                        fit: BoxFit.cover,                      ),                    ),                    if (isCanWork)                      AppButtonIcon(                        padding: EdgeInsets.zero,                        onPressed: () {                          deleteImage(media.src);                        },                        icon: PhosphorIcons.trash_fill,                        iconColor: context.themeColor.red,                      ),                  ],                ),              ),              if(!showImageAttachBehind && !isEnoughMedias)              ImagesAttachment(                amountMedia: medias.length,                maxMedia: maxImage,                uploadImage: uploadImage,                width: width,              ),            ],          ],        );      },    );  }}