import 'package:hlshop/all_file/all_file.dart';class ImagesAttachment extends StatelessWidget {  const ImagesAttachment({    super.key,    required this.width,    required this.amountMedia,    required this.maxMedia,    this.label,    required this.uploadImage,  });  final double width;  final String? label;  final int maxMedia;  final int amountMedia;  final void Function()? uploadImage;  @override  Widget build(BuildContext context) {    return CardCupertinoEffect(      onPressed: uploadImage,      child: SizedBox(        height: 80,        width: width,        child: DottedBorderIcon(          colorBorder: context.themeColor.primary,          child: Column(            mainAxisSize: MainAxisSize.min,            children: [              Icon(                Icons.camera_alt_outlined,                color: context.themeColor.greyNeutral,              ),              (label ?? 'Tải ảnh'.tr())                  .text                  .bodySmall(context).colorNeutral(context)                  .make()                  .fittedBox(),              '($amountMedia/$maxMedia)'                  .text                  .bodySmall(context).colorNeutral(context)                  .make()                  .fittedBox(),            ],          ).centered(),        ),      ),    );  }}