import 'package:hlshop/all_file/all_file.dart';class ImagesAttachment extends StatelessWidget {  const ImagesAttachment({    super.key,    required this.width,    required this.uploadImage,  });  final double width;  final void Function()? uploadImage;  @override  Widget build(BuildContext context) {    return CardCupertinoEffect(      onPressed: uploadImage,      child: SizedBox(        height: 80,        width: width,        child: DottedBorderIcon(          padding: Dimens.edge_XS,          colorBorder: context.themeColor.primary,          child: Center(            child: Column(              mainAxisSize: MainAxisSize.min,              children: [                Icon(                  Icons.camera_alt_outlined,                  color: context.themeColor.greyLighter,                ),                'Thêm ảnh'.tr().text.bodySmall(context).make(),              ],            ),          ),        ),      ),    );  }}