import 'package:app_ui_kit/all_file/app_ui_kit_all_file.dart';import 'package:hlshop/all_file/all_file.dart';class ProductCommentInputField extends StatelessWidget {  const ProductCommentInputField({    super.key,    required this.formControlName,  });  final String formControlName;  static final Map<String, ValidationMessageFunction>      commentValidationMessages = {    ValidationMessage.maxLength: (_) => 'Vui lòng nhập dưới 50 ký tự'.tr(),  };  @override  Widget build(BuildContext context) {    return Column(      children: [        ReactiveControlBuilder(          controlName: formControlName,          builder: (Widget child, FormControl<String> control, value) {            final value = control.value as String;            return Column(              children: [                Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    'Viết đánh giá sản phẩm này'                        .tr()                        .text                        .bodyMedium(context)                        .color(context.themeColor.grey)                        .make(),                    '{}/50'                        .tr(                          args: [                            value.length.toString(),                          ],                        )                        .text                        .bodyMedium(context)                        .colorNeutral(context)                        .make(),                  ],                ),              ],            );          },        ),        Gaps.vGap16,        AppTextFieldReactive(              showErrors: (control) => control.invalid,              key: Key(formControlName),              maxLines: 3,              keyboardType: TextInputType.multiline,              formControlName: formControlName,              validationMessages: commentValidationMessages,              hintText: 'Nhập'.tr(),            ),      ],    );  }}