import 'package:hlshop/all_file/all_file.dart';class RatingProduct extends StatelessWidget {  const RatingProduct({    super.key,    required this.product,    this.onPressed,    this.args = const ProductItemArgs(),    this.attributeValues,  });  final ProductEntity product;  final VoidCallback? onPressed;  final String? attributeValues;  final ProductItemArgs args;  @override  Widget build(BuildContext context) {    return CardCupertinoEffect(      onPressed: onPressed,      child: SizedBox(        height: 90,        child: Row(          mainAxisSize: MainAxisSize.min,          crossAxisAlignment: CrossAxisAlignment.stretch,          children: [            Container(              decoration: BoxDecoration(                border: Border.all(                  color: context.themeColor.greyLighter,                ),                borderRadius: Dimens.rad_border_circular_XS2,              ),              clipBehavior: Clip.hardEdge,              child: AppImg(                product.img,              ),            ).aspectRatio(0.9),            Gaps.hGap12,            _buildContent(              context: context,              attributeValues: attributeValues,            ).py8().expand(),          ],        ),      ),    );  }  Column _buildContent({    required BuildContext context,    required String? attributeValues,  }) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        product.name?.textAuto            .titleMedium(context)            .maxLines(2)            .ellipsis            .make()            .pb2()            .minHeight(22),        'Phân loại: {}'.tr(args: [attributeValues ?? '']).text.maxLines(1).ellipsis.make().flexible(),      ].filterNotNullList(),    );  }}